'use strict';

import './App.css';
import React from 'react';
import ReactDOM from 'react-dom';
import client from './client';
import follow from './follow'; // function to hop multiple links by "rel"

const root = '/api';

export default class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {tabletopSystems: [], attributes: [], pageSize: 2, links: {}};
		this.updatePageSize = this.updatePageSize.bind(this);
		this.onCreate = this.onCreate.bind(this);
		this.onDelete = this.onDelete.bind(this);
		this.onNavigate = this.onNavigate.bind(this);
	}

	loadFromServer(pageSize) {
    	follow(client, root, [
    		{rel: 'tabletopSystems', params: {size: pageSize}}]
    	).then(tabletopSystemCollection => {
    		return client({
    			method: 'GET',
    			path: tabletopSystemCollection.entity._links.profile.href,
    			headers: {'Accept': 'application/schema+json'}
    		}).then(schema => {
    			this.schema = schema.entity;
    			return tabletopSystemCollection;
    		});
    	}).done(tabletopSystemCollection => {
    		this.setState({
    			tabletopSystems: tabletopSystemCollection.entity._embedded.tabletopSystems,
    			attributes: Object.keys(this.schema.properties),
    			pageSize: pageSize,
    			links: tabletopSystemCollection.entity._links});
    	});
    }

    onCreate(newTabletopSystem) {
    	follow(client, root, ['tabletopSystems']).then(tabletopSystemCollection => {
    		return client({
    			method: 'POST',
    			path: tabletopSystemCollection.entity._links.self.href,
    			entity: newTabletopSystem,
    			headers: {'Content-Type': 'application/json'}
    		})
    	}).then(response => {
    		return follow(client, root, [
    			{rel: 'tabletopSystems', params: {'size': this.state.pageSize}}]);
    	}).done(response => {
    		if (typeof response.entity._links.last !== "undefined") {
    			this.onNavigate(response.entity._links.last.href);
    		} else {
    			this.onNavigate(response.entity._links.self.href);
    		}
    	});
    }

	onDelete(tabletopSystem) {
		client({method: 'DELETE', path: tabletopSystem._links.self.href}).done(response => {
			this.loadFromServer(this.state.pageSize);
		});
	}

	onNavigate(navUri) {
		client({method: 'GET', path: navUri}).done(tabletopSystemCollection => {
			this.setState({
				tabletopSystems: tabletopSystemCollection.entity._embedded.tabletopSystems,
				attributes: this.state.attributes,
				pageSize: this.state.pageSize,
				links: tabletopSystemCollection.entity._links
			});
		});
	}

	updatePageSize(pageSize) {
		if (pageSize !== this.state.pageSize) {
			this.loadFromServer(pageSize);
		}
	}

	componentDidMount() {
	    this.loadFromServer(this.state.pageSize);
	}

	render() {
		return (
			<div className="App">
				<header className="App-header">
				<CreateDialog attributes={this.state.attributes} onCreate={this.onCreate}/>
				<TabletopSystemList tabletopSystems={this.state.tabletopSystems}
							  links={this.state.links}
							  pageSize={this.state.pageSize}
							  onNavigate={this.onNavigate}
							  onDelete={this.onDelete}
							  updatePageSize={this.updatePageSize}/>
				</header>
			</div>
		);
	}
}

class CreateDialog extends React.Component {

	constructor(props) {
		super(props);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleSubmit(e) {
		e.preventDefault();
		const newTabletopSystem = {};
		this.props.attributes.forEach(attribute => {
			newTabletopSystem[attribute] = ReactDOM.findDOMNode(this.refs[attribute]).value.trim();
		});
		this.props.onCreate(newTabletopSystem);

		// clear out the dialog's inputs
		this.props.attributes.forEach(attribute => {
			ReactDOM.findDOMNode(this.refs[attribute]).value = '';
		});

		// Navigate away from the dialog to hide it.
		window.location = "#";
	}

	render() {
		const inputs = this.props.attributes.map(attribute =>
			<p key={attribute}>
				<input type="text" placeholder={attribute} ref={attribute} className="field"/>
			</p>
		);

		return (
			<div>
				<a href="#createTabletopSystem">Create</a>

				<div id="createTabletopSystem" className="modalDialog">
					<div>
						<a href="#" title="Close" className="close">X</a>

						<h2>Create new tabletop system</h2>

						<form>
							{inputs}
							<button onClick={this.handleSubmit}>Create</button>
						</form>
					</div>
				</div>
			</div>
		)
	}
}

class TabletopSystemList extends React.Component {

	constructor(props) {
		super(props);
		this.handleNavFirst = this.handleNavFirst.bind(this);
		this.handleNavPrev = this.handleNavPrev.bind(this);
		this.handleNavNext = this.handleNavNext.bind(this);
		this.handleNavLast = this.handleNavLast.bind(this);
		this.handleInput = this.handleInput.bind(this);
	}

	handleInput(e) {
		e.preventDefault();
		const pageSize = ReactDOM.findDOMNode(this.refs.pageSize).value;
		if (/^[0-9]+$/.test(pageSize)) {
			this.props.updatePageSize(pageSize);
		} else {
			ReactDOM.findDOMNode(this.refs.pageSize).value =
				pageSize.substring(0, pageSize.length - 1);
		}
	}

	handleNavFirst(e){
		e.preventDefault();
		this.props.onNavigate(this.props.links.first.href);
	}

	handleNavPrev(e) {
		e.preventDefault();
		this.props.onNavigate(this.props.links.prev.href);
	}

	handleNavNext(e) {
		e.preventDefault();
		this.props.onNavigate(this.props.links.next.href);
	}

	handleNavLast(e) {
		e.preventDefault();
		this.props.onNavigate(this.props.links.last.href);
	}

	render() {
		const tabletopSystems = this.props.tabletopSystems.map(tabletopSystem =>
			<TabletopSystem key={tabletopSystem._links.self.href} tabletopSystem={tabletopSystem} onDelete={this.props.onDelete}/>
		);

		const navLinks = [];
		if ("first" in this.props.links) {
			navLinks.push(<button key="first" onClick={this.handleNavFirst}>&lt;&lt;</button>);
		}
		if ("prev" in this.props.links) {
			navLinks.push(<button key="prev" onClick={this.handleNavPrev}>&lt;</button>);
		}
		if ("next" in this.props.links) {
			navLinks.push(<button key="next" onClick={this.handleNavNext}>&gt;</button>);
		}
		if ("last" in this.props.links) {
			navLinks.push(<button key="last" onClick={this.handleNavLast}>&gt;&gt;</button>);
		}

		return (
			<div>
				<input ref="pageSize" defaultValue={this.props.pageSize} onInput={this.handleInput}/>
				<table>
					<tbody>
						<tr>
							<th>Name</th>
							<th>Description</th>
							<th></th>
						</tr>
						{tabletopSystems}
					</tbody>
				</table>
				<div>
					{navLinks}
				</div>
			</div>
		)
	}
}

class TabletopSystem extends React.Component {

	constructor(props) {
		super(props);
		this.handleDelete = this.handleDelete.bind(this);
	}

	handleDelete() {
		this.props.onDelete(this.props.tabletopSystem);
	}

	render() {
		return (
			<tr>
				<td>{this.props.tabletopSystem.name}</td>
				<td>{this.props.tabletopSystem.description}</td>
				<td>
					<button onClick={this.handleDelete}>Delete</button>
				</td>
			</tr>
		)
	}
}
